        commit_message_negative_regex = push_rule.commit_message_negative_regex,
        author_email_regex = push_rule.author_email_regex,
        file_name_regex = push_rule.file_name_regex,
        updated_at = push_rule.updated_at;
    END IF;
  END IF;

  RETURN NEW;
END;
$$;


